import React, { Component } from 'react'
import Title from './Title'
import Like from './Like'
import Dislike from './Dislike'
import Options from './Options'

class Main extends Component {
    constructor(){
        super();
        this.state ={
            options:[{
                id:0,
                name:'Mango',
                description:'King of fruits',
                imgLink:'data:image/jpeg;base64,'
            },{
                id:1,
                name:'Apple',
                description:'Healtiest fruit',
                imgLink:'data:image/jpeg;base64,'
            },{
                id:2,
                name:'Orange',
                description:'Brightest fruit',
                imgLink:'data:image/jpeg;base64,'
            },{
                id:3,
                name:'Jackfruit',
                description:'Brightest fruit',
                imgLink:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRdvPs2kXPprCm_C7rHjv_Oclz0OGByCD0_rQ&usqp=CAU"
            }],
            like:[],
            dislike:[]
        }
        this.dislike = this.dislike.bind(this);
        this.like = this.like.bind(this);
    }

    dislike(dislikeItem){
        this.setState((state) =>({
            options : state.options.filter((item) => item != dislikeItem),
            dislike : state.dislike.concat([dislikeItem])
        }));
    }

    like(likeItem){
        this.setState((state) =>({
            options: state.options.filter((item) => item != likeItem),
            like: state.like.concat([likeItem])
        }));
    }
    render() {
        return (
            <div>
                <Title title={'Lets find your likes and dislikes'} />
                <div className="main-container">
                    <Dislike dislikes={this.state.dislike}/>
                    <Options options={this.state.options} onDislike ={this.dislike} onLike={this.like}/>
                    <Like  likes={this.state.like}/>
                </div>
            </div>
        )
    }
}

export default Main